script:
frappe.ui.form.on('Asset', {
    custodian: function(frm) {
        if (frm.doc.custodian) {
            frappe.call({
                method: "frappe.client.get",
                args: {
                    doctype: "Employee",
                    name: frm.doc.custodian
                },
                callback: function(r) {
                    if (r.message) {
                        let emp = r.message;
                        let display = `${emp.employee_number || ''} - ${emp.employee_name || emp.employee || ''}`;
                        frm.set_value('custodian_details', display);
                    }
                }
            });
        } else {
            frm.set_value('custodian_details', '');
        }
    }
});

frappe.ui.form.on('Daily Sample Request', {
    refresh(frm) {
        // Add button on form
        frm.add_custom_button('Create Sample Work Order', () => {
            if (!frm.doc.name) {
                frappe.msgprint(__('Please save the Daily Sample Request first.'));
                return;
            }

            // Create new Sample Work Order with link field prefilled
            frappe.new_doc('Sample Work Order', {
                fetch_daily_sample_request: frm.doc.name
            });
        }, __('Actions')); // optional group label for better UI
    }
});

// ===============================
// SAMPLE WORK ORDER - CLIENT SCRIPT
// ===============================

// Central map (use one source only)
const processTypeMap = {
    "Design": "Pre-Production",
    "Program": "Pre-Production",
    "Knitting": "Production",
    "Knitting Manual": "Production",
    "Linking": "Production",
    "Mending": "Production",
    "Wash": "Post-Production",
    "Print": "Post-Production",
    "Iron": "Post-Production",
    "Sewing": "Post-Production",
    "Zip / Button": "Post-Production",
    "Finishing": "Post-Production"
};


// ===============================
// PARENT DOCTYPE
// ===============================
frappe.ui.form.on('Sample Work Order', {

    // When form loads (existing document)
    onload(frm) {
        if (!frm.doc.workstation_details) return;

        frm.doc.workstation_details.forEach(row => {
            if (row.process_name && !row.process_type) {
                frappe.model.set_value(
                    row.doctype,
                    row.name,
                    "process_type",
                    processTypeMap[row.process_name] || ""
                );
            }
        });
    },

    // Extra safety before save
    before_save(frm) {
        frm.doc.workstation_details.forEach(row => {
            if (row.process_name) {
                frappe.model.set_value(
                    row.doctype,
                    row.name,
                    "process_type",
                    processTypeMap[row.process_name] || ""
                );
            }
        });
    }
});


// ===============================
// CHILD TABLE
// ===============================
frappe.ui.form.on('Workstation Details', {

    // When process name changes
    process_name(frm, cdt, cdn) {
        const row = locals[cdt][cdn];

        if (row.process_name) {
            frappe.model.set_value(
                cdt,
                cdn,
                "process_type",
                processTypeMap[row.process_name] || ""
            );
        } else {
            frappe.model.set_value(
                cdt,
                cdn,
                "process_type",
                ""
            );
        }
    }
});

// =========================================================
// Order Sheet Entry â€“ Professional Suite
// =========================================================

frappe.ui.form.on("Order Sheet Entry", {
    refresh(frm) {
        run_all_calculations(frm);
    },
    validate(frm) {
        run_all_calculations(frm);
    },
    before_save(frm) {
        run_all_calculations(frm);
    },
    qty_details_add(frm) { sync_qty_to_tables(frm); },
    qty_details_remove(frm) { sync_qty_to_tables(frm); },
    sweater_quantity(frm) {
        (frm.doc.elastic_details || []).forEach(row => {
            calculate_elastic_total(frm, row.doctype, row.name);
        });
    }
});

// Target: Yarn Table
frappe.ui.form.on('Order Sheet Yarn Detail', {
    // FIXED: Changed consumption_per_pcs to consumption to match your field name
    consumption: (frm) => calculate_yarn_totals(frm),
    wastage: (frm) => calculate_yarn_totals(frm),
    quantity: (frm) => calculate_yarn_totals(frm),
    yarn_details_add: (frm) => calculate_yarn_totals(frm),
    yarn_details_remove: (frm) => calculate_yarn_totals(frm)
});

// Target: Trims & Threads
frappe.ui.form.on('Order Sheet Trim Detail', {
    consumption: (frm, cdt, cdn) => run_smart_calc(frm, cdt, cdn),
    wastage: (frm, cdt, cdn) => run_smart_calc(frm, cdt, cdn),
    quantity: (frm, cdt, cdn) => run_smart_calc(frm, cdt, cdn),
    trim_details_remove: (frm) => calculate_smart_grand_totals(frm),
    sewing_thread_booking_remove: (frm) => calculate_smart_grand_totals(frm)
});

// --- Calculation Engines ---

function run_all_calculations(frm) {
    calculate_yarn_totals(frm);
    calculate_smart_grand_totals(frm);
}

function calculate_yarn_totals(frm) {
    let total_qty = (frm.doc.qty_details || []).reduce((sum, r) => sum + flt(r.qty), 0);
    frm.set_value("sweater_quantity", total_qty);

    let running_total_lbs = 0;

    if (frm.doc.yarn_details) {
        frm.doc.yarn_details.forEach(row => {
            // 1. Calculate total in Grams first
            let row_grams = (flt(row.consumption) * flt(row.quantity)) * (1 + flt(row.wastage) / 100);

            // 2. Convert Grams to Lbs (Divide by 453.59237)
            let row_lbs = row_grams / 453.59237;

            // Set individual row total to 'total_yarn' in Lbs
            frappe.model.set_value(row.doctype, row.name, "total_yarn", flt(row_lbs, 3));

            running_total_lbs += row_lbs;
        });
    }

    // Set parent totals in Lbs
    frm.set_value("total_required_yarn", flt(running_total_lbs, 3));

    let display_val = flt(running_total_lbs, 3).toLocaleString(undefined, {
        minimumFractionDigits: 3, 
        maximumFractionDigits: 3
    }) + " Lbs";

    frm.set_value("total_yarn_value", display_val);

    // Refresh fields to show updated Pound values
    frm.refresh_field("total_required_yarn");
    frm.refresh_field("total_yarn_value");
    frm.refresh_field("yarn_details");
}

function run_smart_calc(frm, cdt, cdn) {
    let row = frappe.get_doc(cdt, cdn);
    let qty = flt(row.quantity);
    let cons = flt(row.consumption);
    let wast = flt(row.wastage);
    let result = 0;

    if (row.parentfield === "trim_details") {
        result = (cons * qty * (1 + wast / 100)) / 1728; 
    } else if (cons > 0) {
        result = (qty / cons) * (1 + wast / 100);
    }

    frappe.model.set_value(cdt, cdn, "total_trim_required", flt(result, 3));
    calculate_smart_grand_totals(frm);
}

function calculate_smart_grand_totals(frm) {
    let btn_total = (frm.doc.trim_details || []).reduce((sum, r) => sum + flt(r.total_trim_required), 0);
    let thr_total = (frm.doc.sewing_thread_booking || []).reduce((sum, r) => sum + flt(r.total_trim_required), 0);

    frm.set_value('total_button_required', flt(btn_total, 3));
    frm.set_value('total_sewing_thred_required', flt(thr_total, 3));
}

function sync_qty_to_tables(frm) {
    let qty_rows = frm.doc.qty_details || [];
    let processed_sizes = qty_rows.map(r => r.size).filter(Boolean);
    let target_tables = ["trim_details", "sewing_thread_booking", "yarn_details"];

    target_tables.forEach(table_name => {
        if(!frm.doc[table_name]) return;

        qty_rows.forEach(qty_row => {
            let row = (frm.doc[table_name] || []).find(r => r.size === qty_row.size);
            if (!row) {
                row = frm.add_child(table_name);
                row.size = qty_row.size;
            }
            frappe.model.set_value(row.doctype, row.name, "quantity", flt(qty_row.qty));
        });

        frm.doc[table_name] = (frm.doc[table_name] || []).filter(r => processed_sizes.includes(r.size));
        frm.refresh_field(table_name);
    });

    run_all_calculations(frm);
    (frm.doc.trim_details || []).forEach(r => run_smart_calc(frm, r.doctype, r.name));
    (frm.doc.sewing_thread_booking || []).forEach(r => run_smart_calc(frm, r.doctype, r.name));
}

function calculate_elastic_total(frm, cdt, cdn) {
    let row = frappe.get_doc(cdt, cdn);
    let grams = (flt(frm.doc.sweater_quantity) * flt(row.elastic_qty)) * (1 + flt(row.elastic_percentage) / 100);
    let lbs = flt(grams / 453.59237, 2);
    frappe.model.set_value(cdt, cdn, "total_quantity", lbs + " Lbs");
}
