frappe.ui.form.on('Asset', {
    custodian: function(frm) {
        if (frm.doc.custodian) {
            frappe.call({
                method: "frappe.client.get",
                args: {
                    doctype: "Employee",
                    name: frm.doc.custodian
                },
                callback: function(r) {
                    if (r.message) {
                        let emp = r.message;
                        let display = `${emp.employee_number || ''} - ${emp.employee_name || emp.employee || ''}`;
                        frm.set_value('custodian_details', display);
                    }
                }
            });
        } else {
            frm.set_value('custodian_details', '');
        }
    }
});

frappe.ui.form.on('Daily Sample Request', {
    refresh(frm) {
        // Add button on form
        frm.add_custom_button('Create Sample Work Order', () => {
            if (!frm.doc.name) {
                frappe.msgprint(__('Please save the Daily Sample Request first.'));
                return;
            }

            // Create new Sample Work Order with link field prefilled
            frappe.new_doc('Sample Work Order', {
                fetch_daily_sample_request: frm.doc.name
            });
        }, __('Actions')); // optional group label for better UI
    }
});

// ===============================
// SAMPLE WORK ORDER - CLIENT SCRIPT
// ===============================

// Central map (use one source only)
const processTypeMap = {
    "Design": "Pre-Production",
    "Program": "Pre-Production",
    "Knitting": "Production",
    "Knitting Manual": "Production",
    "Linking": "Production",
    "Mending": "Production",
    "Wash": "Post-Production",
    "Print": "Post-Production",
    "Iron": "Post-Production",
    "Sewing": "Post-Production",
    "Zip / Button": "Post-Production",
    "Finishing": "Post-Production"
};


// ===============================
// PARENT DOCTYPE
// ===============================
frappe.ui.form.on('Sample Work Order', {

    // When form loads (existing document)
    onload(frm) {
        if (!frm.doc.workstation_details) return;

        frm.doc.workstation_details.forEach(row => {
            if (row.process_name && !row.process_type) {
                frappe.model.set_value(
                    row.doctype,
                    row.name,
                    "process_type",
                    processTypeMap[row.process_name] || ""
                );
            }
        });
    },

    // Extra safety before save
    before_save(frm) {
        frm.doc.workstation_details.forEach(row => {
            if (row.process_name) {
                frappe.model.set_value(
                    row.doctype,
                    row.name,
                    "process_type",
                    processTypeMap[row.process_name] || ""
                );
            }
        });
    }
});


// ===============================
// CHILD TABLE
// ===============================
frappe.ui.form.on('Workstation Details', {

    // When process name changes
    process_name(frm, cdt, cdn) {
        const row = locals[cdt][cdn];

        if (row.process_name) {
            frappe.model.set_value(
                cdt,
                cdn,
                "process_type",
                processTypeMap[row.process_name] || ""
            );
        } else {
            frappe.model.set_value(
                cdt,
                cdn,
                "process_type",
                ""
            );
        }
    }
});

frappe.ui.form.on("Order Sheet Entry", {

    refresh(frm) {
        calculate_totals(frm);
    },

    qty_details_add(frm) {
        sync_qty_to_yarn(frm);
    },

    qty_details_remove(frm) {
        sync_qty_to_yarn(frm);
    }

});


function sync_qty_to_yarn(frm) {
    let qty_rows = frm.doc.qty_details || [];
    let yarn_rows = frm.doc.yarn_details || [];

    // Keep track of sizes we have processed
    let processed_sizes = [];

    qty_rows.forEach(qty_row => {
        let yarn_row = yarn_rows.find(r => r.size === qty_row.size);

        if(!yarn_row) {
            yarn_row = frm.add_child("yarn_details");
            yarn_row.size = qty_row.size;
        }

        processed_sizes.push(qty_row.size);

        // Update quantity using frappe.model.set_value
        frappe.model.set_value(yarn_row.doctype, yarn_row.name, "quantity", flt(qty_row.qty));

        // Calculate total_yarn
        let consumption = flt(yarn_row.consumption_per_pcs || 0);
        let wastage = flt(yarn_row.wastage || 0);
        frappe.model.set_value(yarn_row.doctype, yarn_row.name, "total_yarn", (consumption * flt(qty_row.qty)) * (1 + wastage / 100));
    });

    // Remove any yarn_details rows that are no longer in qty_details
    yarn_rows.forEach(row => {
        if(!processed_sizes.includes(row.size)) {
            frappe.model.delete_doc(row.doctype, row.name);
        }
    });

    frm.refresh_field("yarn_details");
    calculate_totals(frm);
}

function calculate_totals(frm) {
    let total_yarn = 0;
    let total_qty = 0;

    (frm.doc.yarn_details || []).forEach(row => {
        total_yarn += flt(row.total_yarn);
        total_qty += flt(row.quantity);
    });

    frm.set_value("total_required_yarn", total_yarn);
    frm.set_value("sweater_quantity", total_qty);
}


// Parent Doctype Trigger
frappe.ui.form.on("Order Sheet Qty Detail", {

    qty(frm, cdt, cdn) {
        sync_qty_to_yarn(frm);
    },

    size(frm, cdt, cdn) {
        sync_qty_to_yarn(frm);
    }

});

function sync_qty_to_yarn(frm) {
    let qty_rows = frm.doc.qty_details || [];
    let yarn_rows = frm.doc.yarn_details || [];

    let processed_sizes = [];

    qty_rows.forEach(qty_row => {
        // Try to find an existing yarn_details row with the same size
        let yarn_row = yarn_rows.find(r => r.size === qty_row.size);

        if (!yarn_row) {
            // Create new row if it doesn't exist
            yarn_row = frm.add_child("yarn_details");
            yarn_row.size = qty_row.size;
        }

        processed_sizes.push(qty_row.size);

        // Update quantity in yarn_details
        frappe.model.set_value(yarn_row.doctype, yarn_row.name, "quantity", flt(qty_row.qty));

        // Calculate total_yarn based on existing consumption and wastage
        let consumption = flt(yarn_row.consumption_per_pcs || 0);
        let wastage = flt(yarn_row.wastage || 0);
        frappe.model.set_value(yarn_row.doctype, yarn_row.name, "total_yarn", (consumption * flt(qty_row.qty)) * (1 + wastage / 100));
    });

    // Optional: remove yarn_details rows not in qty_details
    // yarn_rows.forEach(row => {
    //     if (!processed_sizes.includes(row.size)) frappe.model.delete_doc(row.doctype, row.name);
    // });

    frm.refresh_field("yarn_details");

    // Optional: recalc parent totals if you have calculate_totals function
    if(typeof calculate_totals === "function") {
        calculate_totals(frm);
    }
}

// Parent Doctype Trigger
frappe.ui.form.on("Order Sheet Entry", {
    total_required_yarn(frm) {
        calculate_colour_totals(frm);
    }
});

// Child Table Triggers
frappe.ui.form.on("Multiple Colour Ratio", {
    percentage(frm, cdt, cdn) {
        calculate_colour_totals(frm);
    },
    weight(frm, cdt, cdn) {
        calculate_colour_totals(frm);
    }
});

// Main Calculation Function
function calculate_colour_totals(frm) {
    const child = frm.doc.multiple_colour_ratio;
    if (!child || child.length === 0) return;

    let total_required = flt(frm.doc.total_required_yarn); // in grams

    // Sum of percentages
    let total_percentage = 0;
    child.forEach(row => {
        total_percentage += flt(row.percentage);
    });

    let sum_qty = 0;

    // Calculate all except last row
    for (let i = 0; i < child.length; i++) {
        let row = child[i];
        let perc = flt(row.percentage);

        let qty_in_grams = (i < child.length - 1) ?
            total_required * (perc / total_percentage) :
            total_required - sum_qty; // last row gets remaining to match total exactly

        // Convert to lbs
        let qty_in_lbs = qty_in_grams / 453.59237;
        qty_in_lbs = parseFloat(qty_in_lbs.toFixed(2));

        frappe.model.set_value(row.doctype, row.name, "total_qty", qty_in_lbs);

        sum_qty += qty_in_grams;
    }

    frm.refresh_field("multiple_colour_ratio");
}

// Trigger when elastic_percentage changes
frappe.ui.form.on("Elastic Details", {
    elastic_percentage(frm, cdt, cdn) {
        calculate_elastic_total(frm, cdt, cdn);
    }
});

// Trigger when parent sweater_quantity changes
frappe.ui.form.on("Order Sheet Entry", {
    sweater_quantity(frm) {
        if(frm.doc.elastic_details) {
            frm.doc.elastic_details.forEach(row => {
                calculate_elastic_total(frm, row.doctype, row.name);
            });
        }
    }
});

function calculate_elastic_total(frm, cdt, cdn) {
    let row = frappe.get_doc(cdt, cdn);

    let parent_qty = flt(frm.doc.sweater_quantity || 0);   // Sweater qty
    let elastic_qty = flt(row.elastic_qty || 0);          // Per pcs elastic qty
    let percentage = flt(row.elastic_percentage || 0);    // Wastage %

    // Total in grams including wastage
    let total_grams = parent_qty * elastic_qty;

    if (percentage > 0) {
        total_grams += total_grams * (percentage / 100);
    }

    // Convert grams to pounds
    let total_pounds = total_grams / 453.59237;

    // Round: if decimal >= 0.5 → round up
    total_pounds = Math.round(total_pounds);

    // Add " Pound" next to the number
    let display_value = total_pounds + " Pound";

    // Set value in child table
    frappe.model.set_value(cdt, cdn, "total_quantity", display_value);

    // Refresh child table
    frm.refresh_field("elastic_details");
}


