frappe.ui.form.on('Order Sheet Trim Detail', {
    // Trigger when any of these fields change
    consumption: calculate_total_trim,
    quantity: calculate_total_trim,
    wastage: calculate_total_trim
});

function calculate_total_trim(frm, cdt, cdn) {
    let row = frappe.get_doc(cdt, cdn);

    let consumption = parseFloat(row.consumption || 0);
    let quantity = parseFloat(row.quantity || 0);
    let wastage = parseFloat(row.wastage || 0);

    // Calculate total trim required for this row
    row.total_trim_required = (consumption * quantity) * (1 + wastage / 100);

    // Refresh the child table field
    frm.refresh_field('trim_details');

    // Now calculate grand total for all rows
    let grand_total = 0;
    frm.doc.trim_details.forEach(r => {
        grand_total += parseFloat(r.total_trim_required || 0);
    });

    // Set the parent field
    frm.set_value('total_button_required', grand_total);
}

// =============================
// Order Sheet Entry – Client Script (Cleaned & De-duplicated)
// =============================

// -------- Parent Doctype --------
frappe.ui.form.on("Order Sheet Entry", {
    refresh(frm) {
        calculate_totals(frm);
    },

    qty_details_add(frm) {
        sync_qty_to_yarn(frm);
    },

    qty_details_remove(frm) {
        sync_qty_to_yarn(frm);
    },

    sweater_quantity(frm) {
        // Recalculate elastic totals when sweater qty changes
        (frm.doc.elastic_details || []).forEach(row => {
            calculate_elastic_total(frm, row.doctype, row.name);
        });
    },

    total_required_yarn(frm) {
        calculate_colour_totals(frm);
    }
});

// -------- Qty Child Table --------
frappe.ui.form.on("Order Sheet Qty Detail", {
    qty(frm) {
        sync_qty_to_yarn(frm);
    },
    size(frm) {
        sync_qty_to_yarn(frm);
    }
});

// -------- Yarn Sync Logic --------
function sync_qty_to_yarn(frm) {
    let qty_rows = frm.doc.qty_details || [];
    let yarn_rows = frm.doc.yarn_details || [];

    let processed_sizes = [];

    qty_rows.forEach(qty_row => {
        let yarn_row = yarn_rows.find(r => r.size === qty_row.size);

        if (!yarn_row) {
            yarn_row = frm.add_child("yarn_details");
            yarn_row.size = qty_row.size;
        }

        processed_sizes.push(qty_row.size);

        frappe.model.set_value(yarn_row.doctype, yarn_row.name, "quantity", flt(qty_row.qty));

        let consumption = flt(yarn_row.consumption_per_pcs || 0);
        let wastage = flt(yarn_row.wastage || 0);

        let total = (consumption * flt(qty_row.qty)) * (1 + wastage / 100);
        frappe.model.set_value(yarn_row.doctype, yarn_row.name, "total_yarn", total);
    });

    // Remove orphan yarn rows
    yarn_rows.forEach(row => {
        if (!processed_sizes.includes(row.size)) {
            frappe.model.delete_doc(row.doctype, row.name);
        }
    });

    frm.refresh_field("yarn_details");
    calculate_totals(frm);
}

// -------- Parent Totals --------
function calculate_totals(frm) {
    let total_yarn = 0;
    let total_qty = 0;

    (frm.doc.yarn_details || []).forEach(row => {
        total_yarn += flt(row.total_yarn);
        total_qty += flt(row.quantity);
    });

    frm.set_value("total_required_yarn", total_yarn);
    frm.set_value("sweater_quantity", total_qty);
}

// -------- Multiple Colour Ratio --------
frappe.ui.form.on("Multiple Colour Ratio", {
    percentage(frm) {
        calculate_colour_totals(frm);
    },
    weight(frm) {
        calculate_colour_totals(frm);
    }
});

function calculate_colour_totals(frm) {
    let rows = frm.doc.multiple_colour_ratio || [];
    if (!rows.length) return;

    let total_required = flt(frm.doc.total_required_yarn);
    let total_percentage = rows.reduce((t, r) => t + flt(r.percentage), 0);

    let used_grams = 0;

    rows.forEach((row, index) => {
        let grams = (index < rows.length - 1)
            ? total_required * (flt(row.percentage) / total_percentage)
            : total_required - used_grams;

        used_grams += grams;

        let lbs = parseFloat((grams / 453.59237).toFixed(2));
        frappe.model.set_value(row.doctype, row.name, "total_qty", lbs);
    });

    frm.refresh_field("multiple_colour_ratio");
}

// -------- Elastic Details --------
frappe.ui.form.on("Elastic Details", {
    elastic_percentage(frm, cdt, cdn) {
        calculate_elastic_total(frm, cdt, cdn);
    }
});

function calculate_elastic_total(frm, cdt, cdn) {
    let row = frappe.get_doc(cdt, cdn);

    let parent_qty = flt(frm.doc.sweater_quantity);
    let elastic_qty = flt(row.elastic_qty);
    let wastage = flt(row.elastic_percentage);

    let grams = parent_qty * elastic_qty;
    grams += grams * (wastage / 100);

    let pounds = Math.round(grams / 453.59237);
    frappe.model.set_value(cdt, cdn, "total_quantity", `${pounds} Pound`);

    frm.refresh_field("elastic_details");
}

// -------- Trim Details --------
frappe.ui.form.on('Order Sheet Trim Detail', {
    consumption: calculate_total_trim,
    quantity: calculate_total_trim,
    wastage: calculate_total_trim
});

function calculate_total_trim(frm, cdt, cdn) {
    let row = frappe.get_doc(cdt, cdn);

    let total = (flt(row.consumption) * flt(row.quantity)) * (1 + flt(row.wastage) / 100);
    row.total_trim_required = total;

    frm.refresh_field('trim_details');

    let grand_total = (frm.doc.trim_details || [])
        .reduce((t, r) => t + flt(r.total_trim_required), 0);

    frm.set_value('total_button_required', grand_total);
}
