frappe.ui.form.on('Asset', {
    custodian: function(frm) {
        if (frm.doc.custodian) {
            frappe.call({
                method: "frappe.client.get",
                args: {
                    doctype: "Employee",
                    name: frm.doc.custodian
                },
                callback: function(r) {
                    if (r.message) {
                        let emp = r.message;
                        let display = `${emp.employee_number || ''} - ${emp.employee_name || emp.employee || ''}`;
                        frm.set_value('custodian_details', display);
                    }
                }
            });
        } else {
            frm.set_value('custodian_details', '');
        }
    }
});

frappe.ui.form.on('Daily Sample Request', {
    refresh(frm) {
        // Add button on form
        frm.add_custom_button('Create Sample Work Order', () => {
            if (!frm.doc.name) {
                frappe.msgprint(__('Please save the Daily Sample Request first.'));
                return;
            }

            // Create new Sample Work Order with link field prefilled
            frappe.new_doc('Sample Work Order', {
                fetch_daily_sample_request: frm.doc.name
            });
        }, __('Actions')); // optional group label for better UI
    }
});

// ===============================
// SAMPLE WORK ORDER - CLIENT SCRIPT
// ===============================

// Central map (use one source only)
const processTypeMap = {
    "Design": "Pre-Production",
    "Program": "Pre-Production",
    "Knitting": "Production",
    "Knitting Manual": "Production",
    "Linking": "Production",
    "Mending": "Production",
    "Wash": "Post-Production",
    "Print": "Post-Production",
    "Iron": "Post-Production",
    "Sewing": "Post-Production",
    "Zip / Button": "Post-Production",
    "Finishing": "Post-Production"
};


// ===============================
// PARENT DOCTYPE
// ===============================
frappe.ui.form.on('Sample Work Order', {

    // When form loads (existing document)
    onload(frm) {
        if (!frm.doc.workstation_details) return;

        frm.doc.workstation_details.forEach(row => {
            if (row.process_name && !row.process_type) {
                frappe.model.set_value(
                    row.doctype,
                    row.name,
                    "process_type",
                    processTypeMap[row.process_name] || ""
                );
            }
        });
    },

    // Extra safety before save
    before_save(frm) {
        frm.doc.workstation_details.forEach(row => {
            if (row.process_name) {
                frappe.model.set_value(
                    row.doctype,
                    row.name,
                    "process_type",
                    processTypeMap[row.process_name] || ""
                );
            }
        });
    }
});


// ===============================
// CHILD TABLE
// ===============================
frappe.ui.form.on('Workstation Details', {

    // When process name changes
    process_name(frm, cdt, cdn) {
        const row = locals[cdt][cdn];

        if (row.process_name) {
            frappe.model.set_value(
                cdt,
                cdn,
                "process_type",
                processTypeMap[row.process_name] || ""
            );
        } else {
            frappe.model.set_value(
                cdt,
                cdn,
                "process_type",
                ""
            );
        }
    }
});

