// =========================================================
// Order Sheet Entry – Absolute Auto-Sync & Fill
// =========================================================

frappe.ui.form.on("Order Sheet Entry", {
    refresh(frm) {
        run_all_calculations(frm);
    },
    validate(frm) {
        run_all_calculations(frm);
    },
    before_save(frm) {
        run_all_calculations(frm);
    },
    qty_details_add(frm) { sync_qty_to_tables(frm); },
    qty_details_remove(frm) { sync_qty_to_tables(frm); }
});

frappe.ui.form.on("Order Sheet Qty Detail", {
    qty: function(frm) {
        sync_qty_to_tables(frm);
    }
});

// Updated Listeners for Elastic Table
frappe.ui.form.on('Elastic Details', {
    elastic_qty: (frm, cdt, cdn) => calculate_elastic_total(frm, cdt, cdn),
    wastage: (frm, cdt, cdn) => calculate_elastic_total(frm, cdt, cdn)
});

frappe.ui.form.on('Order Sheet Trim Detail', {
    consumption: (frm, cdt, cdn) => run_smart_calc(frm, cdt, cdn),
    wastage: (frm, cdt, cdn) => run_smart_calc(frm, cdt, cdn)
});

function sync_qty_to_tables(frm) {
    let qty_rows = frm.doc.qty_details || [];
    let processed_sizes = qty_rows.map(r => r.size).filter(Boolean);

    let target_tables = [
        "yarn_details", 
        "trim_details", 
        "sewing_thread_booking", 
        "elastic_details", 
        "zipper_booking"
    ];

    target_tables.forEach(table_name => {
        if (!frm.get_field(table_name)) return;
        let existing_rows = frm.doc[table_name] || [];

        qty_rows.forEach(qty_row => {
            if (!qty_row.size) return;
            let row = existing_rows.find(r => r.size === qty_row.size);
            if (!row) {
                row = frm.add_child(table_name);
                row.size = qty_row.size;
            }
            frappe.model.set_value(row.doctype, row.name, "quantity", flt(qty_row.qty));
        });

        let rows_to_keep = (frm.doc[table_name] || []).filter(r => processed_sizes.includes(r.size));
        frm.doc[table_name] = rows_to_keep;
        frm.refresh_field(table_name);
    });

    run_all_calculations(frm);
}

function run_all_calculations(frm) {
    let total_sweater_qty = (frm.doc.qty_details || []).reduce((sum, r) => sum + flt(r.qty), 0);
    frm.set_value("sweater_quantity", total_sweater_qty);

    calculate_yarn_totals(frm);

    ["trim_details", "sewing_thread_booking", "zipper_booking"].forEach(table => {
        (frm.doc[table] || []).forEach(r => run_smart_calc(frm, r.doctype, r.name));
    });

    (frm.doc.elastic_details || []).forEach(r => calculate_elastic_total(frm, r.doctype, r.name));

    calculate_smart_grand_totals(frm);
}

// FIXED ELASTIC CALCULATION
function calculate_elastic_total(frm, cdt, cdn) {
    let row = frappe.get_doc(cdt, cdn);

    // 1. Calculate Individual Row
    let grams = (flt(frm.doc.sweater_quantity) * flt(row.elastic_qty)) * (1 + flt(row.wastage) / 100);
    let lbs = flt(grams / 453.59237, 3);

    // Set the value in the child row
    frappe.model.set_value(cdt, cdn, "total_quantity", lbs + " Lbs");

    // 2. Calculate Grand Total for Parent Field
    let grand_total_elastic = 0;
    (frm.doc.elastic_details || []).forEach(r => {
        if (r.total_quantity) {
            // Remove " Lbs" string and convert to number for summing
            let numeric_val = flt(r.total_quantity.toString().replace(" Lbs", ""));
            grand_total_elastic += numeric_val;
        }
    });

    // Update the parent field
    frm.set_value("total_elastic_required", flt(grand_total_elastic, 3));
}
function run_smart_calc(frm, cdt, cdn) {
    let row = frappe.get_doc(cdt, cdn);
    let qty = flt(row.quantity);
    let cons = flt(row.consumption);
    let wast = flt(row.wastage);
    let result = 0;

    if (cons > 0) {
        if (row.parentfield === "trim_details") {
            result = (cons * qty * (1 + wast / 100)) / 1728;
        } else {
            result = (qty / cons) * (1 + wast / 100);
        }
    }
    frappe.model.set_value(cdt, cdn, "total_trim_required", flt(result, 3));
}

function calculate_yarn_totals(frm) {
    let running_total_lbs = 0;
    (frm.doc.yarn_details || []).forEach(row => {
        let row_grams = (flt(row.consumption) * flt(row.quantity)) * (1 + flt(row.wastage) / 100);
        let row_lbs = row_grams / 453.59237;
        frappe.model.set_value(row.doctype, row.name, "total_yarn", flt(row_lbs, 3));
        running_total_lbs += row_lbs;
    });
    frm.set_value("total_required_yarn", flt(running_total_lbs, 3));
}

function calculate_smart_grand_totals(frm) {
    let thr_total = (frm.doc.sewing_thread_booking || []).reduce((sum, r) => sum + flt(r.total_trim_required), 0);
    frm.set_value('total_sewing_thred_required', flt(thr_total, 3));
}
